DROP TABLE IF EXISTS `t_orders`;
CREATE TABLE `t_orders` (
  `ORDER_ID` varchar(255) NOT NULL DEFAULT '' COMMENT '订单ID',
  `DELIVER_UNIT` varchar(50) DEFAULT NULL COMMENT '物流单位',
  `DELIVER_NUM` varchar(100) DEFAULT NULL COMMENT '物流单号',
  `PRICE_NOW` int(11) DEFAULT NULL COMMENT '商品单价（现价），小数点右移两位',
  `PRODUCT_MODEL` varchar(50) DEFAULT NULL COMMENT '商品规格',
  `PRODUCT_COUNT` double DEFAULT NULL COMMENT '商品数量',
  `DELIVER_PRICE` int(11) DEFAULT NULL COMMENT '运费',
  `SALE_PRICE` int(11) DEFAULT NULL COMMENT '优惠金额，单位分',
  `REAL_MONEY` int(11) DEFAULT NULL COMMENT '实付金额',
  `STATUS` varchar(100) DEFAULT NULL COMMENT '订单状态',
  `ORDER_TIME` datetime DEFAULT NULL COMMENT '下单时间',
  `PAY_TIME` datetime DEFAULT NULL COMMENT '付款时间',
  `DELIVER_TIME` datetime DEFAULT NULL COMMENT '发货时间',
  `FINISH_TIME` datetime DEFAULT NULL COMMENT '交易完成时间',
  `REMARK` varchar(255) DEFAULT NULL COMMENT '备注',
  `PRODUCT_ID` varchar(255) DEFAULT NULL COMMENT '商品ID，外键',
  `STORE_ID` varchar(255) DEFAULT NULL COMMENT '店铺ID,外键',
  `USER_ID` varchar(255) DEFAULT NULL COMMENT '用户ID，外键',
  `RECEIVER_ID` int(11) DEFAULT NULL COMMENT '收货地址ID,外键',
  PRIMARY KEY (`ORDER_ID`),
  KEY `FK_ORDER_PRODUCT_ID` (`PRODUCT_ID`),
  KEY `FK_ORDER_STORE` (`STORE_ID`),
  KEY `FK_ORDER_USER_ID` (`USER_ID`),
  KEY `FK_ORDER_RECEIVER_ID` (`RECEIVER_ID`),
  CONSTRAINT `FK_ORDER_PRODUCT_ID` FOREIGN KEY (`PRODUCT_ID`) REFERENCES `t_products` (`PID`),
  CONSTRAINT `FK_ORDER_RECEIVER_ID` FOREIGN KEY (`RECEIVER_ID`) REFERENCES `t_receiver_addr` (`ID`),
  CONSTRAINT `FK_ORDER_STORE` FOREIGN KEY (`STORE_ID`) REFERENCES `t_stores` (`SID`),
  CONSTRAINT `FK_ORDER_USER_ID` FOREIGN KEY (`USER_ID`) REFERENCES `t_users` (`USER_ID`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='订单表';

